var OutputStreamCallback =  Java.type("org.apache.nifi.processor.io.OutputStreamCallback");
var IOUtils = Java.type("org.apache.commons.io.IOUtils");
var StandardCharsets = Java.type("java.nio.charset.StandardCharsets");

var flowFile = session.get();

if (flowFile != null) {

	var attributes = flowFile.getAttributes();

	/*
	outputStream = session.write(flowFile);
	outputStream.write(attributes.end_dt.getBytes(StandardCharsets.UTF_8));
	outputStream.close();
	*/

	flowFile = session.write(flowFile,
		new OutputStreamCallback(function(outputStream) {
				outputStream.write(attributes.end_dt.getBytes(StandardCharsets.UTF_8));
		}));

	session.transfer(flowFile, REL_SUCCESS);
}

import org.apache.commons.io.IOUtils
import java.nio.charset.*
def flowFile = session.get();
if (flowFile == null) {
    return;
}
def slurper = new groovy.json.JsonSlurper()
def attrs = [:] as Map<String,String>
session.read(flowFile,
    { inputStream ->
        def text = IOUtils.toString(inputStream, StandardCharsets.UTF_8)
        def obj = slurper.parseText(text)
        obj.each {k,v ->
           attrs[k] = v.toString()
        }
    } as InputStreamCallback)

def text = ''
// Cast a closure with an outputStream parameter to OutputStreamCallback
flowFile = session.write(flowFile, {outputStream ->
  outputStream.write(text.getBytes(StandardCharsets.UTF_8))
} as OutputStreamCallback)

flowFile = session.putAllAttributes(flowFile, attrs)
session.transfer(flowFile , REL_SUCCESS)

var flowFile = session.get();

if (flowFile != null) {


	var attributes = flowFile.getAttributes();
	var accessTokenStr = attributes.accessTokenAddrMstrInsert;

	var obj = JSON.parse(accessTokenStr);

	var accessToken = obj.access_token;

	// Compute for token validity, if within expiry, near expiry or expired
	var timeDiff = new Date().getTime() - obj.issued_at;

	var tokenTimeOut = parseInt(attributes.tokenTimeOut);
	var tolerance = parseInt(attributes.tokenTimeOutTolerance);
	var tokenExpiryStatus = 'expired';
	if (timeDiff < (tokenTimeOut - tolerance)) {
		var tokenExpiryStatus = 'valid';
	} else if ( (timeDiff >= (tokenTimeOut - tolerance)) && (timeDiff < tokenTimeOut) ) {
		var tokenExpiryStatus = 'near-expired';
	}

	var attrMap = {
		'access_token': accessToken,
		'salesForceInstanceUrl': obj.instance_url,
		'salesForceRestUrl': attributes.salesForceRestUrl,
		'time_since_last_token': timeDiff/1000 + ' seconds',
		'token_expiry_status': tokenExpiryStatus
	};

	flowFile = session.putAllAttributes(flowFile, attrMap)
	flowFile = session.removeAttribute(flowFile, 'accessTokenTemp')

	session.transfer(flowFile, REL_SUCCESS);
}

import java.util.regex.Pattern
import java.nio.charset.StandardCharsets
import java.time.format.DateTimeFormatter
import java.time.temporal.ChronoUnit
import java.time.LocalDateTime
import java.time.ZonedDateTime
import java.time.ZoneId

def flowFile = session.get()

if (!flowFile) return

try {

	def p = Pattern.compile("(?s)(^.*\$)")

	def timezone = flowFile.getAttribute('timezone')
	def tokenTimeOut = flowFile.getAttribute('tokenTimeOut')
	def tokenTimeOutTolerance = flowFile.getAttribute('tokenTimeOutTolerance')
	def sqlRetries = flowFile.getAttribute('sqlRetries')
	def sfdcRetries = flowFile.getAttribute('sfdcRetries')
	def original_uuid = flowFile.getAttribute("uuid")
	def source_uuid = flowFile.getAttribute("source_uuid")
	def flow = flowFile.getAttribute("flow")
	def country = flowFile.getAttribute("country")

	def dtf = DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss")
	def zone = ZoneId.of(timezone)

	def end_dt_str = flowFile.getAttribute('original_end_dt')

	def zdt_now = ZonedDateTime.now(zone)
	def zdt_end = ZonedDateTime.of(LocalDateTime.parse(end_dt_str, dtf), zone)
	def zdt_start = zdt_end.minusHours(1).minusSeconds(5)

	session.removeAllAttributes(flowFile, p)

	attrMap = [
						 'timezone': timezone,
						 'tokenTimeOut': tokenTimeOut,
						 'tokenTimeOutTolerance': tokenTimeOutTolerance,
						 'sqlRetries': sqlRetries,
						 'sfdcRetries': sfdcRetries,
						 'now_dt': zdt_now.format(dtf),
						 'start_time': zdt_now.format(dtf),
						 'start_dt': zdt_start.format(dtf),
						 'end_dt': zdt_end.format(dtf),
						 'original_end_dt': zdt_end.format(dtf),
						 'source_uuid': source_uuid,
						 'flow': flow,
						 'fragment.count': String.valueOf(1),
						 'fragment.index': String.valueOf(0),
						 'fragment.identifier': original_uuid,
						 'country':country
						]

	def override_start_dt_prop = null
	def override_end_dt_prop = null

	try {
		override_start_dt_prop = override_start_dt.value
		override_end_dt_prop = override_end_dt.value
	} catch(groovy.lang.MissingPropertyException e) {
		override_start_dt_prop = null
		override_end_dt_prop = null
	}

	if (override_start_dt_prop && override_end_dt_prop) {
		attrMap.put('override_start_dt', override_start_dt_prop)
		attrMap.put('override_end_dt', override_end_dt_prop)
	} else if (flowFile.getAttribute("override_start_dt") && flowFile.getAttribute("override_end_dt")) {
		attrMap.put('override_start_dt', flowFile.getAttribute("override_start_dt"))
		attrMap.put('override_end_dt', flowFile.getAttribute("override_end_dt"))
	}

	flowFile = session.putAllAttributes(flowFile, attrMap)

	flowFile = session.write(flowFile, { outputStream ->
		outputStream.write("".getBytes(StandardCharsets.UTF_8))
	} as OutputStreamCallback)

	session.transfer(flowFile, REL_SUCCESS)

} catch (e) {
	log.error('ExecuteScript failure: "Bootstrap" processor', e)
	session.transfer(flowFile, REL_FAILURE)
}

