import java.time.format.DateTimeFormatter
import java.time.temporal.ChronoUnit
import java.time.temporal.TemporalUnit
import java.time.ZonedDateTime
import java.time.ZoneId

def flowFile = session.get()

if (!flowFile) return

try {

	def timezone = flowFile.getAttribute('timezone')
	def dtf = DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss")
	def zone = ZoneId.of(timezone)

	def zdt_now = ZonedDateTime.now(zone)
	def zdt_end = zdt_now.truncatedTo(ChronoUnit.HOURS)

	attrMap = [
						 'now_dt': zdt_now.format(dtf),
						 'end_dt': zdt_end.format(dtf),
						 'original_end_dt': zdt_end.format(dtf)
						]

	flowFile = session.putAllAttributes(flowFile, attrMap)
	session.transfer(flowFile, REL_SUCCESS)
} catch (e) {
	log.error('ExecuteScript failure: "GetCurrentHour processor"', e)
	session.transfer(flowFile, REL_FAILURE)
}
